mapper.select:
SELECT ev.id, to_char(ev.occurred_on, 'YYYY-MM-DD') AS occurred_on_date, to_char(ev.occurred_on, 'HH24:MI:SS') AS occurred_on_time, ST_X(ev.location) AS longitude, ST_Y(ev.location) AS latitude, COALESCE(co01.name, 'Unspecified') AS closest_country, COALESCE(co02.name, 'Unspecified') AS territorial_water_status, COALESCE(co03.name, 'Unspecified') AS vessel_flag_country FROM event AS ev LEFT JOIN country AS co01 ON co01.cow_id = ev.location_closest_country_id LEFT JOIN country AS co02 ON co02.cow_id = ev.location_water_status_country_id LEFT JOIN country AS co03 ON co03.cow_id = ev.vessel_flag_country_id
ev.occurred_on >= #{eventFilter.beginDate}::timestamp AND ev.occurred_on <= #{eventFilter.endDate}::timestamp
AND ev.location_closest_country_id IN
#{id}
AND ev.locatio_water_status_country_id IN
#{id}
AND ev.vessel_flag_country_id IN
#{id}
ORDER BY ev.occurred_on ASC;

SELECT id, cow_id, name, abbreviation
FROM country
ORDER BY name ASC;
